function Addcantsleep()
    if (getconnections or get_signal_cons) then
        for i, v in pairs((getconnections or get_signal_cons)(_S.LocalPlayer.Idled)) do
            if v["Disable"] then
                v["Disable"](v)
            elseif v["Disconnect"] then
                v["Disconnect"](v)
            end
        end
    end
end

