    local C_MONEY = "#55FF55" -- Green (for Price)
    local C_EGG   = "#55AAFF" -- Blue (for Egg Name)

    -- 3. HELPER FUNCTIONS

    -- Adds commas to numbers
    local function formatNumber(n)
        return tostring(n):reverse():gsub("%d%d%d", "%1,"):reverse():gsub("^,", "")
    end

    -- Helper to wrap text in color tags easily
    local function colorText(text, color)
        return '<font color="' .. color .. '">' .. text .. '</font>'
    end

    -- 4. BUILD THE TEXT
    local lines = {}

    -- HEADER (Name in Bold and Gold)
    table.insert(lines, "<b>" .. colorText("ðŸ¾ " .. string.upper(_name), C_TITLE) .. "</b>")
    table.insert(lines, colorText("----------------", "#444444")) -- Dark grey divider

    -- EGG SOURCE (New Requirement)
    if eggname then
        table.insert(lines, colorText("â€¢ Found in: ", C_LABEL) .. colorText(eggname, C_EGG))
    end

    -- RARITY
    if pet.Rarity then
        -- Optional: specific colors for specific rarities
        local rColor = C_VAL
        if pet.Rarity == "Mythical" then rColor = "#FF55FF" end  -- Pink for Mythical
        if pet.Rarity == "Legendary" then rColor = "#FFAA00" end -- Orange for Legendary

        table.insert(lines, colorText("â€¢ Rarity: ", C_LABEL) .. colorText(pet.Rarity, rColor))
    end

    -- DESCRIPTION
    if pet.Description and pet.Description ~= "" then
        table.insert(lines, colorText("â€¢ Desc: ", C_LABEL) .. colorText(pet.Description, C_VAL))
    end

    -- PASSIVES
    if pet.Passives and type(pet.Passives) == "table" and #pet.Passives > 0 then
        local passiveList = table.concat(pet.Passives, ", ")
        table.insert(lines, colorText("â€¢ Passives: ", C_LABEL) .. colorText(passiveList, C_VAL))
    end

    -- SELL PRICE
    if pet.SellPrice then
        local priceStr = "$" .. formatNumber(pet.SellPrice)
        table.insert(lines, colorText("â€¢ Sell Price: ", C_LABEL) .. colorText(priceStr, C_MONEY))
    end

    -- MOVEMENT SPEED
    if pet.MovementSpeed then
        table.insert(lines, colorText("â€¢ Speed: ", C_LABEL) .. colorText(pet.MovementSpeed, C_VAL))
    end

    -- MAX HUNGER
    if pet.DefaultHunger then
        table.insert(lines, colorText("â€¢ Max Hunger: ", C_LABEL) .. colorText(pet.DefaultHunger, C_VAL))
    end


    table.insert(lines, _Helper.GetPassiveInfo(_name))

    -- 5. RETURN
    return table.concat(lines, "\n")
end


