local function ParseWholeNumber(input)
    -- Check input is valid (not nil/empty)
    if input == nil or (type(input) == "string" and input:match("^%s*$")) then
        return nil
    end

    -- Try converting to number
    local num = tonumber(input)
    if not num then
        return nil
    end

    -- Only allow whole numbers (integers)
    if num % 1 ~= 0 then
        return nil
    end

    return num
end

local function ParseWeightNumber(input)
    -- Check input is valid (not nil/empty)
    if input == nil or (type(input) == "string" and input:match("^%s*$")) then
        return nil
    end

    -- Try converting to number
    local num = tonumber(input)
    if not num then
        return nil
    end

    return num
end

local function ParseWeightNumber2d(input)
    -- Check input is valid (not nil/empty)
    if input == nil or (type(input) == "string" and input:match("^%s*$")) then
        return nil
    end

    -- Try converting to number
    local num = tonumber(input)
    if not num then
        return nil
    end

    -- Format to 2 decimals string, then convert back to number
    -- This rounds 10.556 -> 10.56 and keeps 10 -> 10
    return tonumber(string.format("%.2f", num))
end

