_Helper.ShortName = function(str, max)
    max = max or 5
    if #str > max then
        return str:sub(1, max) .. "..."
    else
        return str
    end
end

_Helper.ShortNameNoDots = function(str, max)
    max = max or 3
    if #str > max then
        return str:sub(1, max)
    else
        return str
    end
end

_Helper.CountTable = function(t)
    local n = 0
    for _ in pairs(t) do
        n = n + 1
    end
    return n
end


