                    if not _object then continue end
                    ShovelManager.Fruit.DeleteFruit(_object)
                    current_del = current_del + 1
                    task.wait(0.7)
                end

                task.wait(0.1)
                unequipTools()

                ShovelManager.Fruit.UpdateStatusFruit("âœ… Removed fruits. Looking for more.. please wait.")
