            end

            if is_weight_mode then
                if has_weight and has_mut then
                    all_passed = false
                    continue
                end
            else
                if has_mut then
                    all_passed = false
                    continue
                end
            end

            if not has_mut then
                all_passed = false
            end

            if is_weight_mode and not has_weight then
                all_passed = false
            end

            table.insert(tbl_pets, _uuid)
        end

        if FSettings.mut_system.max_lvl_batch then
            if not all_passed then
                tbl_pets = {}
