_PlantsManager.StringToVector3 = function(str)
    if not str then return nil end

    local x, y, z = str:match("([^,]+),([^,]+),([^,]+)")
    if not x or not y or not z then return nil end

    return Vector3.new(tonumber(x), tonumber(y), tonumber(z))
end

