TaskManager.task_letsdig = task.spawn(function()
    while true do
        task.wait(1)
        if not FSettings.halloween.auto_dig then
            task.wait(2)
            continue
        end

        if Varz.IS_HATCHING then
            task.wait(4)
            continue
        end

        Varz.IS_DIG = true
        pcall(function()
            _Helper.DigUseTickets()
        end)
        Varz.IS_DIG = false

        local cc = _Helper.GetModelsFromGrid()

        if #cc == 0 then
            --warn("Event not active")
            _Helper.ResetDig()
            task.wait(20)
            continue
        end

        if _Helper.remain_digs <= 0 then
            task.wait(5)
            --warn("No digs left")
            continue
        end

        Varz.IS_DIG = true
        pcall(function()
            _Helper.DigUseTickets()
        end)
        Varz.IS_DIG = false

        for _, ob in ipairs(cc) do
            local gx = ob.x
            local gy = ob.y
            local cf = ob.targetCFrame

            if Varz.IS_HATCHING then
                break
            end

            if _Helper.GetDigsLeft() <= 0 then
                break
            end

            _Helper.FireDigEvent(gx, gy, cf)
            task.wait(2)
        end
    end
end)

