-- Helper function to organize the grid based on the selected shape
_Helper.SortPositionsByShape = function(positions, center, shape)
    -- 1. Random Shuffle (Default)
    if shape == "random" then
        for i = #positions, 2, -1 do
            local j = math.random(i)
            positions[i], positions[j] = positions[j], positions[i]
        end
        return
    end

    -- 2. Geometric Sorting
    table.sort(positions, function(a, b)
        -- Calculate X and Z offsets explicitly for position A
        local dxA = a.X - center.X
        local dzA = a.Z - center.Z

        -- Calculate X and Z offsets explicitly for position B
        local dxB = b.X - center.X
        local dzB = b.Z - center.Z

        if shape == "circle" then
            -- Sort by standard Euclidean distance (Fill center outward)
            local distSqA = dxA ^ 2 + dzA ^ 2
            local distSqB = dxB ^ 2 + dzB ^ 2
            return distSqA < distSqB
        elseif shape == "square" then
            -- Sort by Chebyshev distance (Concentric squares)
            local maxDistA = math.max(math.abs(dxA), math.abs(dzA))
            local maxDistB = math.max(math.abs(dxB), math.abs(dzB))
            return maxDistA < maxDistB
        elseif shape == "heart" then
            -- Heart Equation: (x^2 + z^2 - 1)^3 - x^2 * z^3 <= 0
            local scale = 0.15

            local function getHeartVal(dx, dz)
                local x = dx * scale
                local z = -(dz * scale) + 0.5 -- Flip Z and apply offset
                local eq = (x ^ 2 + z ^ 2 - 1) ^ 3 - (x ^ 2 * (z ^ 3))
                return eq
            end

            local valA = getHeartVal(dxA, dzA)
            local valB = getHeartVal(dxB, dzB)

            -- Priority: Points DEEP inside the heart (negative values) come first
            return valA < valB
        end

        -- Fallback: Sort by Euclidean distance if shape is unknown
        return (dxA ^ 2 + dzA ^ 2) < (dxB ^ 2 + dzB ^ 2)
    end)
end


_Helper.IsTimeUp = function(startTime, secs)
    if not startTime or not secs then return true end
    return (os.clock() - startTime) >= secs
end

_Helper.GetTimeLeft = function(startTime, duration)
    -- 1. Safety Check: Ensure inputs exist and are numbers.
    -- If inputs are invalid, return 0 (implies time is immediately up).
    if type(startTime) ~= "number" or type(duration) ~= "number" then
        return 0
    end

    -- 2. Calculation: Determine how much time has passed.
    local elapsed = os.clock() - startTime
    local remaining = duration - elapsed

    return math.max(0, remaining)
end

_Helper.GetPing = function()
    -- 1. Default to LocalPlayer if no target is provided
    local player = _S.LocalPlayer

    -- 2. Safety Check: Ensure the player object is valid
    if not player or not player.Parent then
        return 0
    end

    -- 3. Get Ping: Returns time in seconds (e.g., 0.050 for 50ms)
    -- We wrap this in pcall just in case of engine edge cases/permissions
    local success, pingSeconds = pcall(function()
        return player:GetNetworkPing()
    end)

    if not success then return 0 end

    -- 4. Convert to ms and round to nearest whole number
    return math.floor((pingSeconds * 1000) + 0.5)
end

_Helper.GetSafePing = function()
    local lowst = 0.0001
    if not FSettings.sync_pingmode then
        return lowst
    end

    local ok, result = pcall(function()
        local player = _S.LocalPlayer
        local rawPing = (player and player:GetNetworkPing()) or 0
        return math.clamp(rawPing, lowst, 7)
    end)

    return ok and result or lowst
end

