    local activepets = FarmManager.GetActivePetsUUIDS()
    local ls_pets = {}
    local targets = FSettings.mut_system.targetteam or {}
    local allowed_uuids = {}
    for _, uuid in ipairs(targets) do allowed_uuids[uuid] = true end

