end


TaskManager.Targets_max = {}

TaskManager.gift_loops = task.spawn(function()
    while true do
        Varz.IS_GIFT = false

        task.wait(2)
        local ui_text = TaskManager.GiftSystem.UpdateUiGiftSystem

        if not FarmManager.IsDataFullyLoaded() or not FarmManager.IsFarmFullyLoaded() then
            ui_text("â³ Waiting for farm to load.")
            task.wait(5)
            continue
        end


        if not FSettings.giftpets.enabled_gift_pets then
            ui_text("ðŸ”´ Not Enabled")
            task.wait(2)
            continue
        end

        if _Helper.IsSellAllUnFav() and FSettings.is_running then
            ui_text("ðŸŸ¡ Sell All Mode is active for hatching, unable to use gift feature.")
            task.wait(3)
            continue
        end

        if Varz.IS_HATCHING then
            ui_text("ðŸŸ¡ Hatching")
            task.wait(3)
            continue
        end

        local allowed_list = FSettings.giftpets.allow_pet_list or {}
        if next(allowed_list) == nil then
            ui_text("ðŸ”´ No pet types selected to send. Please select a pet type.")
            task.wait(5)
            continue
        end


        local pets = TaskManager.GiftSystem.GetAllPetsForGifting()

        if #pets == 0 then
            ui_text("âŒ No pets to gift.")
            task.wait(5)
            continue
        end

        local targetPlayer = nil
        local current_playername = "-"
        for _name, value in pairs(FSettings.giftpets.allow_player_targets) do
            if TaskManager.Targets_max[_name] then
                -- This user has full inventory
                continue
            end

            local playerx = Varz.IsPlayerActiveUsingName(_name)
            if playerx then
                targetPlayer = playerx
                current_playername = _name
                break
            end
        end

        if not targetPlayer then
            -- print("No player to send to")
            ui_text("ðŸ”´ No players selected or found to send to or user has max inventory.")
            task.wait(5)
            continue
        end


        ui_text("ðŸ¤– Found pets: " .. #pets)

        local delay = 3
        local tries = 0
        for index, datax in ipairs(pets) do
            task.wait()

            local uuid = datax.pet_uuid
            local pet_tool = datax.pet_tool
            if not pet_tool then
                continue
            end

            local playerx = Varz.IsPlayerActiveUsingName(current_playername)
            if not playerx then
                ui_text("âŒ Player not found. ")
                task.wait(3)
                break
            end

            if tries >= 9 then
                break
            end

            if not IsToolHeldNew(pet_tool) then
                unequipTools()
                task.wait(0.3)
            end

            if Varz.IS_HATCHING or not FSettings.giftpets.enabled_gift_pets then break end

            if not targetPlayer then
                break
            end

            Varz.IS_GIFT = true

            if InventoryManager.IsPetFav(pet_tool) then
                ui_text("â¤ï¸ remove fav from pet. ")
                if FSettings.giftpets.allow_fav then
                    -- ungift
                    MakeFruitsFavSingle(pet_tool)
                    task.wait(1)
                else
                    ui_text("ðŸ¤– Unable to send fav pet - Setting not enabled to unfav ")
                    task.wait(1)
                    continue
                end
            end

            local e = EquipToolOnChar(pet_tool)
            if e == false then
                --continue
            end

            local petNameS = pet_tool.Name or "Unknown"

            ui_text("ðŸ¤– Sending pet: " .. petNameS .. " - Left:" .. #pets)


            local success, res = pcall(function()
                --print("Sending to  " .. targetPlayer.Name)

                TaskManager.GiftSystem.SendGift(targetPlayer)
                task.wait(3)

                if Varz.target_max_inventory then
                    TaskManager.Targets_max[current_playername] = true
                    print("Target max inventory ", current_playername)
                    Varz.target_max_inventory = false
                    tries = 100 -- ends next cycle
                end

                unequipTools()
            end)

            if not success then
                warn("gift error: ", res)
            end

