                else
                    FallEventManager.UpdateStatsText("â˜¹ï¸ No fruits available.")
                    task.wait(2.5)
                end
                task.wait(0.1)
            else
                FallEventManager.UpdateStatsText("âŒ Failed to find fruit category! restart game.")
                task.wait(3)
                --print("fruits category not found")
            end
        end
    end)
