    end
    return false
end

TaskManager.loop_smithman = task.spawn(function()
    while true do
        task.wait(0.5)

        -- event not available
        if true then break end



        if not FarmManager.IsDataFullyLoaded() or not FarmManager.IsFarmFullyLoaded() then
            EventsManager.Furnace.UpdateUIStatus("âšª Farm still loading.")
            task.wait(10)
            continue
        end

        if Varz.IsPaused() then
            task.wait(math.random(2, 5))
            continue
        end

        local FindEventModel = EventsManager.Furnace.FindEventModel()
        if not FindEventModel then
            EventsManager.Furnace.UpdateUIStatus("âŒ Event not found.")
            continue
        end

        if not FSettings.craftevent.smith_auto then
            EventsManager.Furnace.UpdateUIStatus("ðŸ”´ Not enabled or running.")
            task.wait(5)
            continue
        end

        task.wait(0.1)

        if Varz.IS_CRAFTING then
            task.wait(4)
            continue
        end



        EventsManager.Furnace.UpdateUIStatus("ðŸŸ¢ Running.")

        if Varz.IS_HATCHING then
            EventsManager.Furnace.UpdateUIStatus("ðŸŸ¡ Hatching.")
            task.wait(3)
            continue
        end


        local delay_between = 1.1

        local egg = EventsManager.Furnace.EquipEggToSubmit()
        if not egg then
            EventsManager.Furnace.UpdateUIStatus("âŒ No eggs found.")
            task.wait(3)
            continue
        end

        if TaskManager.is_busy_smith() then
            task.wait(3)
            continue
        end

        while not TaskManager.is_busy_smith() do
            EventsManager.Furnace.UpdateUIStatus("âœ… Submit Egg.")
            task.wait(0.3)
            EventsManager.Furnace.SubmitEggHeld()

            task.wait(delay_between)



            local gear = EventsManager.Furnace.EquipGearToSubmit()
            if not gear then
                EventsManager.Furnace.UpdateUIStatus("âŒ No gears found.")
                task.wait(3)
                continue
            end
            EventsManager.Furnace.UpdateUIStatus("âœ… Submit Gear.")
            task.wait(0.3)
            EventsManager.Furnace.SubmitGearHeld()
            task.wait(delay_between)

            local fruit = EventsManager.Furnace.EquipFruitToSubmit()
            if not fruit then
                EventsManager.Furnace.UpdateUIStatus("âŒ No fruits found.")
                task.wait(3)
                continue
            end
            EventsManager.Furnace.UpdateUIStatus("âœ… Submit Fruit.")
            task.wait(0.3)
            EventsManager.Furnace.SubmitFruitHeld()
            task.wait(delay_between)
            break
        end
    end
end)





-- #quests #quest #questloop
task.spawn(function()
    while true do
        task.wait(3)

        if not FarmManager.IsDataFullyLoaded() or not FarmManager.IsFarmFullyLoaded() then
            task.wait(5)
            continue
        end

        if FOtherSettings.is_fall_questline_spin then
            for i = 1, 2, 1 do
                TaskManager.Quests.QuestAutoSpin()
                task.wait(0.5)
            end
        end

        if not FOtherSettings.is_fall_questline_auto then
            EventQuestsManager.UpdateStatsText(
                "<font color='#FF0000'>ðŸ”´ Quests not enabled or runningâ€¦</font>")
            task.wait(1)
            continue
        end

        EventQuestsManager.UpdateStatsText("ðŸŸ¢ Active and runningâ€¦")

        local tasks, visuals = TaskManager.Quests.GetQuestsTodo()
        if not tasks then
            EventQuestsManager.UpdateStatsInfo("â³ Waiting for tasks...")
            continue
        end



        -- harvest any
        TaskManager.Quests.HarvestSingle()

        EventQuestsManager.UpdateStatsInfo(visuals)

        for _, item in ipairs(tasks) do
            local action         = item.action
            local current_amount = item.current_amount
            local target_amount  = item.target_amount
            local needed         = item.needed
            local target_item    = item.target_item

            if not FOtherSettings.is_fall_questline_auto then
                break
            end

            if action == "EarnSheckles" then
                local success, fail = pcall(function()
                    return TaskManager.Quests.ActionEarnSheckles(target_item, needed)
                end)

                if not success then
                    warn("Error" .. action .. ": ", fail)
                end
                task.wait(2)
                continue
            end

            if action == "Plant" then
                local success, fail = pcall(function()
                    return TaskManager.Quests.ActionPlant(target_item, needed)
                end)

                if not success then
                    warn("Error" .. action .. ": ", fail)
                end
                task.wait(2)
                continue
            end

            if action == "Harvest" then
                local success, fail = pcall(function()
                    return TaskManager.Quests.ActionHarvest(target_item, needed)
                end)

                if not success then
                    warn("Error" .. action .. ": ", fail)
                end
                task.wait(2)
                continue
            end
        end
    end
end)




